import React from 'react';
import './Payment.css';


var payment = {
    imgSrc: 'https://s3.amazonaws.com/codecademy-content/programs/react/ravenous/pizza.jpg', 
    type: 'Electricity',
    period: 'date -> date',
    paymentDate: 'date',
    user: 'user',
    amount: 'amount'
}
class Payment extends React.Component {
    render() {
        return (
            <div className="Payment">
            <div className="image-container">
                <img src={payment.imgSrc} alt=''/>
            </div>
            <h2>{payment.type}</h2>
            <div className="Payment-information">
                <div className="Payment-address">
                <p>Amount:</p>
                <p>Period:</p>
                <p>This payment made by {payment.user} on:</p>
                </div>
                <div className="Payment-reviews">
                <h3>${payment.amount}</h3>
                <h3 className="rating">{payment.period}</h3>
                <p>{payment.paymentDate}</p>
                </div>
            </div>
            </div>
        )
    }
}

export default Payment;
